mapper.insert:
	INSERT INTO USER_TB (
			   IDX
 			 , ID
			 , PASS
			 , NAME
			 ) 
		VALUES ( 
				(SELECT IDX
        	      FROM (
			 	        SELECT IFNULL(MAX(CAST(IDX AS UNSIGNED)), 0) + 1 AS IDX
			  			  FROM PROJECT_TB
					   ) A)
		     , #{id}
			 , #{pass}
			 , #{name}
			 )

mapper.select:
SELECT *
  FROM USER_TB
 WHERE ID = #{id}
   AND PASS = #{pass}
